import Link from "next/link";
import Image from "next/image";
import { projects } from "@/lib/data";
import { PlaceHolderImages } from "@/lib/placeholder-images";
import { Badge } from "@/components/ui/badge";
import { slugify } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { ArrowRight, Github, ExternalLink } from "lucide-react";
import { ScrollAnimation } from "@/components/common/scroll-animation";
import { HoverableText } from "../common/hoverable-text";
import DotGrid from "@/components/common/DotGrid";
import RotatingText from "@/components/common/RotatingText";
import TiltedCard from "@/components/common/tilted-card";
import ScrollStack, { ScrollStackItem } from "@/components/common/ScrollStack";

export function ProjectsSection() {
  const getImage = (id: string) => {
    return PlaceHolderImages.find(img => img.id === id);
  }

  return (
    <section id="projects" className="py-24 sm:py-32 dark bg-[#4e1d41] text-white relative overflow-visible">
      <div style={{ width: '100%', height: '100%', position: 'absolute', top: 0, left: 0, zIndex: 0, opacity: 0.3 }}>
        <DotGrid
          dotSize={4}
          gap={40}
          baseColor="#9333ea"
          activeColor="#a855f7"
          proximity={120}
          speedTrigger={80}
          shockRadius={200}
          shockStrength={3}
        />
      </div>
      <div className="container mx-auto px-4 relative z-10">
        <div className="text-center mb-16">
          <ScrollAnimation>
            <h2 className="font-headline text-5xl sm:text-6xl tracking-tight">Featured Projects</h2>
            <div className="mt-4 text-2xl text-muted-foreground">
              <RotatingText 
                texts={['Innovative Solutions', 'Creative Designs', 'Technical Excellence', 'Real-World Impact']}
                rotationInterval={3000}
                staggerDuration={0.05}
                className="font-headline text-3xl"
              />
            </div>
            <div className="mt-4 text-2xl text-muted-foreground max-w-2xl mx-auto">
              A selection of projects where I've turned <HoverableText imageUrl="data:image/jpeg;base64," imageHint="lightbulb idea" className="font-headline font-bold text-4xl">ideas</HoverableText> into <HoverableText imageUrl="data:image/jpeg;base64," imageHint="abstract reality" className="font-headline font-bold text-4xl">reality</HoverableText>.
            </div>
          </ScrollAnimation>
        </div>
        <ScrollStack
          className="w-full"
          useWindowScroll
          itemDistance={140}
          itemStackDistance={60}
          baseScale={0.9}
          scaleEndPosition="15%"
          blurAmount={4}
        >
          {projects.map((project, index) => {
            const projectImage = getImage(project.image);
            const slug = slugify(project.title);
            const isEven = index % 2 === 0;

            return (
              <ScrollStackItem
                key={project.title}
                itemClassName="bg-white/5 border border-white/10 overflow-hidden text-white"
              >
                <div className="absolute inset-0 bg-gradient-to-b from-[#2b1730]/35 via-[#1b1120]/45 to-[#0f0b16]/55" />
                <div className="relative z-10 grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-16 items-center [text-shadow:0_2px_14px_rgba(0,0,0,0.85)]">
                  <div className={`relative w-full ${isEven ? 'lg:order-1' : 'lg:order-6'}`}>
                    {projectImage && (
                      <div className="flex justify-center px-4">
                        <TiltedCard
                          imageSrc={projectImage.imageUrl}
                          altText={project.title}
                          containerHeight="420px"
                          containerWidth="100%"
                          imageHeight="380px"
                          imageWidth="360px"
                          scaleOnHover={1.05}
                          rotateAmplitude={8}
                          showMobileWarning={false}
                          className="rounded-lg"
                          imageClassName="rounded-lg"
                        />
                      </div>
                    )}
                  </div>
                  <div className={`space-y-4 ${isEven ? 'lg:order-2' : 'lg:order-1'}`}>
                    <div className="w-12 h-12 bg-secondary text-secondary-foreground flex items-center justify-center rounded-full font-headline font-bold text-xl mb-4">
                      {project.logo}
                    </div>
                    <h3 className="font-headline text-4xl tracking-tight">{project.title}</h3>
                    <h4 className="font-headline text-3xl text-muted-foreground">{project.subtitle}</h4>
                    <div className="flex flex-wrap gap-2">
                      {project.tags.map((tag) => (
                        <Badge key={tag} variant="secondary">{tag}</Badge>
                      ))}
                    </div>
                    <p className="text-3xl text-muted-foreground pt-2">{project.description}</p>
                    <div className="flex flex-wrap gap-3 pt-4">
                      <Button asChild className="rounded-full">
                        <Link href={`/projects/${slug}`}>
                          View Case Study <ArrowRight className="ml-2 h-4 w-4" />
                        </Link>
                      </Button>
                      {project.github && (
                        <Button asChild variant="outline" className="rounded-full">
                          <Link href={project.github} target="_blank" rel="noopener noreferrer">
                            <Github className="mr-2 h-4 w-4" /> GitHub
                          </Link>
                        </Button>
                      )}
                      {project.demo && (
                        <Button asChild variant="outline" className="rounded-full">
                          <Link href={project.demo} target="_blank" rel="noopener noreferrer">
                            <ExternalLink className="mr-2 h-4 w-4" /> Live Demo
                          </Link>
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              </ScrollStackItem>
            );
          })}
        </ScrollStack>
      </div>
    </section>
  );
}
